#import <Foundation/Foundation.h>#import "ChatAppController.h"@interface ChatHelperIOSchar* MakeStringCopy (const char* string){    if (string == NULL)        return NULL;        char* res = (char*)malloc(strlen(string) + 1);    strcpy(res, string);    return res;}@endextern "C"{    char* getLocationStatus() {                ChatAppController *chatController = (ChatAppController*)[[UIApplication sharedApplication] delegate];        NSString *locationStatus = [chatController getLocationStatus];                NSLog(@"[Location Status]%@", locationStatus);                return MakeStringCopy([locationStatus UTF8String]);    }    char* getPushMessage() {        ChatAppController *chatController = (ChatAppController*)[[UIApplication sharedApplication] delegate];        NSString *pushMessage = [chatController getPushMessage];                NSLog(@"[Push Message]%@", pushMessage);        return MakeStringCopy([pushMessage UTF8String]);    }}